client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(210, 11%, 96%);
  --muted-foreground: hsl(215, 8%, 43%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(60, 4.8%, 95.9%);
  --secondary-foreground: hsl(24, 9.8%, 10%);
  --accent: hsl(60, 4.8%, 95.9%);
  --accent-foreground: hsl(24, 9.8%, 10%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(20, 14.3%, 4.1%);
  --radius: 0.5rem;

  /* STEP Brand Colors */
  --step-blue: hsl(207, 100%, 40%);
  --step-light-blue: hsl(195, 100%, 50%);
  --step-gray: hsl(210, 17%, 96%);
  --step-dark-gray: hsl(210, 11%, 45%);

  /* Extended Service Colors based on STEP palette */
  --step-cloud: hsl(207, 100%, 40%);     /* Primary blue for Cloud Server */
  --step-hosting: hsl(142, 76%, 36%);    /* Green for Hosting */
  --step-server: hsl(271, 91%, 65%);     /* Purple for Dedicated Server */
  --step-colocation: hsl(25, 95%, 53%);  /* Orange for Colocation */
  --step-email: hsl(263, 70%, 50%);      /* Indigo for Email */
  --step-dlp: hsl(339, 82%, 52%);        /* Pink for DLP */

  /* Typography System */
  --fs-h1: clamp(2.25rem, 2.5vw + 1rem, 3.5rem);
  --fs-h2: clamp(1.875rem, 1.8vw + 0.9rem, 2.75rem);
  --fs-h3: clamp(1.5rem, 1.2vw + 0.7rem, 2rem);
  --fs-lead: clamp(1.125rem, 1vw + 0.5rem, 1.375rem);
  --fs-body: 1rem;
  --fs-small: 0.9375rem;
  --lh-heading: 1.15;
  --lh-lead: 1.6;
  --lh-body: 1.75;
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    font-size: var(--fs-body);
    line-height: var(--lh-body);
    /* Performance optimizations */
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  html {
    scroll-behavior: smooth;
    /* Optimize scroll performance */
    scroll-padding-top: 80px;
  }

  /* Performance optimizations */
  * {
    /* Hardware acceleration for smooth animations */
    transform: translateZ(0);
    backface-visibility: hidden;
    perspective: 1000px;
  }

  /* Optimize images and media */
  img, video {
    will-change: transform;
    transform: translateZ(0);
  }

  /* Smooth focus transitions */
  button, input, textarea, select {
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  }

  /* Reduce motion for accessibility */
  @media (prefers-reduced-motion: reduce) {
    * {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
      scroll-behavior: auto !important;
    }
  }
}

@layer components {
  /* Typography System Components */
  .h1 {
    font-size: var(--fs-h1);
    font-weight: 800;
    line-height: var(--lh-heading);
    letter-spacing: -0.01em;
    text-wrap: balance;
  }

  .h2 {
    font-size: var(--fs-h2);
    font-weight: 800;
    line-height: var(--lh-heading);
    text-wrap: balance;
  }

  .h3 {
    font-size: var(--fs-h3);
    font-weight: 700;
    line-height: var(--lh-heading);
    text-wrap: balance;
  }

  .lead {
    font-size: var(--fs-lead);
    line-height: var(--lh-lead);
    color: var(--muted-foreground);
  }

  .body {
    font-size: var(--fs-body);
    line-height: var(--lh-body);
  }

  .small {
    font-size: var(--fs-small);
    line-height: 1.6;
  }

  .prose-constraint {
    max-width: 72ch;
  }

  .text-balance {
    text-wrap: balance;
  }

  .text-pretty {
    text-wrap: pretty;
  }

  .nowrap {
    white-space: nowrap;
  }

  .section {
    padding-block: 4rem;
  }

  .section-sm {
    padding-block: 3rem;
  }

  .stack {
    display: block;
  }

  .stack > * + * {
    margin-top: 1rem;
  }
}

@layer utilities {
  .step-gradient {
    background: linear-gradient(135deg, hsl(195, 100%, 50%), hsl(207, 100%, 40%));
  }

  /* Onboarding highlight effect */
  .onboarding-highlight {
    position: relative;
    z-index: 41;
  }

  .onboarding-highlight::before {
    content: '';
    position: absolute;
    inset: -8px;
    border: 2px solid #3B82F6;
    border-radius: 12px;
    background: rgba(59, 130, 246, 0.1);
    backdrop-filter: blur(4px);
    animation: highlight-pulse 2s infinite;
    pointer-events: none;
  }

  @keyframes highlight-pulse {
    0%, 100% {
      opacity: 1;
      transform: scale(1);
    }
    50% {
      opacity: 0.7;
      transform: scale(1.02);
    }
  }
}

  .step-hero-gradient {
    background: linear-gradient(135deg, hsl(210, 17%, 96%), hsl(0, 0%, 100%));
    position: relative;
    overflow: hidden;
  }

  .step-hero-gradient::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 191, 255, 0.1), transparent);
    animation: shimmer 3s infinite;
  }

  @keyframes shimmer {
    0% {
      left: -100%;
    }
    100% {
      left: 100%;
    }
  }

  .step-card-hover {
    transition: all 0.3s ease;
    transform: translateY(0);
  }

  .step-card-hover:hover {
    transform: translateY(-8px);
    box-shadow: 0 20px 40px rgba(0, 102, 204, 0.15);
  }

  .step-pulse-animation {
    animation: pulse-glow 2s ease-in-out infinite alternate;
  }

  @keyframes pulse-glow {
    from {
      box-shadow: 0 0 20px rgba(0, 191, 255, 0.4);
    }
    to {
      box-shadow: 0 0 30px rgba(0, 191, 255, 0.8);
    }
  }

  .step-fade-in {
    opacity: 0;
    transform: translateY(20px);
    animation: fadeInUp 0.8s ease-out forwards;
  }

  @keyframes fadeInUp {
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .step-gradient-border {
    position: relative;
    background: white;
    border-radius: 12px;
  }

  .step-gradient-border::before {
    content: '';
    position: absolute;
    inset: 0;
    padding: 2px;
    background: linear-gradient(135deg, hsl(195, 100%, 50%), hsl(207, 100%, 40%));
    border-radius: 12px;
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: exclude;
  }

  /* Ensure proper overflow and positioning for header */
  header {
    overflow: visible !important;
  }

  header .container {
    overflow: visible !important;
  }

  /* Mobile optimization */
  @media (max-width: 640px) {
    .container {
      padding-left: 1rem !important;
      padding-right: 1rem !important;
    }

    header .h-16 {
      height: 4rem !important;
    }

    .step-hero-gradient {
      padding-top: 6rem !important;
    }

    .step-card-hover {
      padding: 1.5rem !important;
    }

    .step-gradient-border {
      margin: 0.5rem !important;
    }
  }

  /* Tablet optimization */
  @media (max-width: 1024px) {
    .step-hero-gradient {
      padding-top: 5rem !important;
    }

    .step-card-hover {
      padding: 2rem !important;
    }
  }

  /* Desktop optimization */
  @media (min-width: 1025px) {
    .step-hero-gradient {
      padding-top: 8rem !important;
    }
  }

  /* Smooth scrolling for anchor links */
  html {
    scroll-behavior: smooth;
  }

  /* Performance optimizations */
  * {
    box-sizing: border-box;
  }

  img {
    max-width: 100%;
    height: auto;
  }

  /* Button animations */
  .button-hover {
    transition: all 0.2s ease;
  }

  .button-hover:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(0, 102, 204, 0.3);
  }

  /* Focus styles for accessibility */
  button:focus,
  a:focus,
  input:focus,
  textarea:focus,
  select:focus {
    outline: 2px solid hsl(207, 100%, 40%);
    outline-offset: 2px;
  }

  /* Reduced motion for accessibility */
  @media (prefers-reduced-motion: reduce) {
    * {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
    }
  }

  /* Header and Navigation Fixes */
  header {
    background-color: white !important;
    border-bottom: 1px solid #e5e7eb !important;
  }

  header nav a,
  header nav button {
    font-size: 1rem !important;
    font-weight: 500 !important;
    color: #374151 !important;
    text-decoration: none !important;
    white-space: nowrap !important;
  }

  header nav a:hover,
  header nav button:hover {
    color: #2563eb !important;
  }

  header .container {
    max-width: 1280px !important;
    margin: 0 auto !important;
    padding: 0 1rem !important;
  }

  /* Prevent mega menu overflow on smaller screens */
  @media (max-width: 1024px) {
    .mega-menu-dropdown {
      left: 0 !important;
      right: 0 !important;
      transform: none !important;
      margin: 0 1rem;
      max-width: calc(100vw - 2rem);
    }
  }

  /* Mobile menu improvements */
  @media (max-width: 768px) {
    .mobile-nav-item {
      padding: 0.75rem 0;
      border-bottom: 1px solid #f3f4f6;
    }

    .mobile-nav-item:last-child {
      border-bottom: none;
    }
  }

  /* Ensure proper header layout */
  .header-container {
    max-width: 1280px;
    margin: 0 auto;
    padding: 0 1rem;
  }

  /* Navigation spacing fixes */
  .nav-desktop {
    gap: 1.5rem;
  }

  @media (min-width: 1280px) {
    .nav-desktop {
      gap: 2rem;
    }
  }

  /* Mega menu styling like Cloudflare */
  .mega-menu-container {
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    width: 900px;
    max-width: 95vw;
    z-index: 9999;
    background: white;
    border: 1px solid #e5e7eb;
    border-radius: 8px;
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  }

  @media (max-width: 1024px) {
    .mega-menu-container {
      width: 95vw;
      left: 0;
      right: 0;
      margin: 0 auto;
      transform: none;
    }
  }

  @media (max-width: 768px) {
    .mega-menu-container {
      width: 98vw;
      margin: 0 1vw;
    }

    .mega-menu-container .p-8 {
      padding: 1.5rem !important;
    }
  }

  /* Smooth mega menu transitions */
  .mega-menu-container {
    opacity: 0;
    visibility: hidden;
    transform: translateX(-50%) translateY(-10px);
    transition: all 0.2s ease-out;
  }

