import { useState } from "react";
import { Button } from "@/components/ui/button";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { devopsArticles } from "@/data/devops-articles-data";

export default function OptimizedDevOpsSeeder() {
  const [isSeeding, setIsSeeding] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const seedMutation = useMutation({
    mutationFn: async () => {
      setIsSeeding(true);
      const results = [];

      for (const article of devopsArticles) {
        try {
          const response = await apiRequest("POST", "/api/articles", article);
          results.push({ success: true, title: article.title, response });
        } catch (error: any) {
          results.push({ success: false, title: article.title, error: error.message });
        }
      }

      return results;
    },
    onSuccess: (results) => {
      const successCount = results.filter(r => r.success).length;
      const errorCount = results.filter(r => !r.success).length;

      toast({
        title: "DevOps Articles Seeded",
        description: `Successfully seeded ${successCount} articles${errorCount > 0 ? `, ${errorCount} failed` : ''}`,
        variant: successCount > 0 ? "default" : "destructive"
      });

      queryClient.invalidateQueries({ queryKey: ["/api/articles"] });
      setIsSeeding(false);
    },
    onError: (error: any) => {
      toast({
        title: "Seeding Failed",
        description: error.message,
        variant: "destructive"
      });
      setIsSeeding(false);
    }
  });

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle>DevOps Articles Seeder</CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <p className="text-sm text-muted-foreground">
          Seed {devopsArticles.length} optimized DevOps articles to the database.
        </p>

        <div className="grid grid-cols-1 gap-2 text-xs">
          {devopsArticles.map((article, index) => (
            <div key={index} className="p-2 bg-muted rounded text-left">
              <div className="font-medium truncate">{article.title}</div>
              <div className="text-muted-foreground">{article.tags?.join(", ")}</div>
            </div>
          ))}
        </div>

        <Button 
          onClick={() => seedMutation.mutate()} 
          disabled={isSeeding || seedMutation.isPending}
          className="w-full"
          data-testid="button-seed-devops"
        >
          {isSeeding || seedMutation.isPending ? "Seeding..." : "Seed DevOps Articles"}
        </Button>
      </CardContent>
    </Card>
  );
}

client/src/components/page-transition.ts.content:
import { motion, AnimatePresence } from "framer-motion";
import { useLocation } from "wouter";
import { ReactNode } from "react";

interface PageTransitionProps {
  children: ReactNode;
}

const pageVariants = {
  initial: {
    opacity: 0,
    y: 20,
    scale: 0.98
  },
  in: {
    opacity: 1,
    y: 0,
    scale: 1
  },
  out: {
    opacity: 0,
    y: -20,
    scale: 1.02
  }
};

const pageTransition = {
  type: "tween",
  ease: "anticipate",
  duration: 0.5
};

export default function PageTransition({ children }: PageTransitionProps) {
  const [location] = useLocation();

  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={location}
        initial="initial"
        animate="in"
        exit="out"
        variants={pageVariants}
        transition={pageTransition}
        className="min-h-screen"
      >
        {children}
      </motion.div>
    </AnimatePresence>
  );
}

// Loading transition component for faster perceived performance
export function LoadingTransition() {
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-white z-50 flex items-center justify-center"
    >
      <motion.div
        animate={{
          scale: [1, 1.2, 1],
          rotate: [0, 180, 360],
        }}
        transition={{
          duration: 1,
          repeat: Infinity,
          ease: "easeInOut",
        }}
        className="w-12 h-12 border-4 border-[hsl(207,100%,40%)] border-t-transparent rounded-full"
      />
    </motion.div>
  );
}

// Stagger animation for lists and grids
export function StaggerContainer({ children, className = "" }: { children: ReactNode; className?: string }) {
  return (
    <motion.div
      initial="hidden"
      animate="visible"
      variants={{
        hidden: { opacity: 0 },
        visible: {
          opacity: 1,
          transition: {
            staggerChildren: 0.1,
            delayChildren: 0.2,
          },
        },
      }}
      className={className}
    >
      {children}
    </motion.div>
  );
}

export function StaggerItem({ children, className = "" }: { children: ReactNode; className?: string }) {
  return (
    <motion.div
      variants={{
        hidden: { opacity: 0, y: 20 },
        visible: { opacity: 1, y: 0 },
      }}
      transition={{ duration: 0.5, ease: "easeOut" }}
      className={className}
    >
      {children}
    </motion.div>
  );
}

// Fade in animation for sections
export function FadeInSection({ children, delay = 0, className = "" }: { 
  children: ReactNode; 
  delay?: number; 
  className?: string; 
}) {
  return (
    <motion.div
      initial={{ opacity: 0, y: 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true, margin: "-100px" }}
      transition={{ 
        duration: 0.6, 
        delay,
        ease: "easeOut" 
      }}
      className={className}
    >
      {children}
    </motion.div>
  );
}

// Scale animation for cards and buttons
export function ScaleOnHover({ children, className = "" }: { children: ReactNode; className?: string }) {
  return (
    <motion.div
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      transition={{ type: "spring", stiffness: 300, damping: 30 }}
      className={className}
    >
      {children}
    </motion.div>
  );
}

client/src/components/interactive-toolti.content:
import { useState, useRef, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { createPortal } from "react-dom";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Info, 
  X, 
  Lightbulb, 
  ArrowRight,
  Star,
  Zap,
  Shield,
  Clock
} from "lucide-react";

interface TooltipContent {
  title: string;
  description: string;
  features?: string[];
  benefits?: string[];
  tips?: string[];
  cta?: {
    text: string;
    action: () => void;
  };
  type?: 'info' | 'feature' | 'benefit' | 'tip';
}

interface InteractiveTooltipProps {
  content: TooltipContent;
  children: React.ReactNode;
  trigger?: 'hover' | 'click';
  position?: 'top' | 'bottom' | 'left' | 'right' | 'auto';
  delay?: number;
  maxWidth?: number;
}

export default function InteractiveTooltip({
  content,
  children,
  trigger = 'hover',
  position = 'auto',
  delay = 300,
  maxWidth = 320
}: InteractiveTooltipProps) {
  const [isVisible, setIsVisible] = useState(false);
  const [calculatedPosition, setCalculatedPosition] = useState({ top: 0, left: 0 });
  const [actualPosition, setActualPosition] = useState<'top' | 'bottom' | 'left' | 'right'>('top');
  const triggerRef = useRef<HTMLDivElement>(null);
  const tooltipRef = useRef<HTMLDivElement>(null);
  const timeoutRef = useRef<NodeJS.Timeout>();

  const calculatePosition = () => {
    if (!triggerRef.current) return;

    const triggerRect = triggerRef.current.getBoundingClientRect();
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;
    const tooltipHeight = 200; // Estimated height
    const tooltipWidth = maxWidth;
    const spacing = 12;

    let top = 0;
    let left = 0;
    let finalPosition: typeof actualPosition = 'top';

    if (position === 'auto') {
      // Auto-determine best position
      const spaceTop = triggerRect.top;
      const spaceBottom = viewportHeight - triggerRect.bottom;
      const spaceLeft = triggerRect.left;
      const spaceRight = viewportWidth - triggerRect.right;

      if (spaceBottom >= tooltipHeight + spacing) {
        finalPosition = 'bottom';
      } else if (spaceTop >= tooltipHeight + spacing) {
        finalPosition = 'top';
      } else if (spaceRight >= tooltipWidth + spacing) {
        finalPosition = 'right';
      } else {
        finalPosition = 'left';
      }
    } else {
      finalPosition = position;
    }

    switch (finalPosition) {
      case 'top':
        top = triggerRect.top - tooltipHeight - spacing;
        left = triggerRect.left + (triggerRect.width - tooltipWidth) / 2;
        break;
      case 'bottom':
        top = triggerRect.bottom + spacing;
        left = triggerRect.left + (triggerRect.width - tooltipWidth) / 2;
        break;
      case 'left':
        top = triggerRect.top + (triggerRect.height - tooltipHeight) / 2;
        left = triggerRect.left - tooltipWidth - spacing;
        break;
      case 'right':
        top = triggerRect.top + (triggerRect.height - tooltipHeight) / 2;
        left = triggerRect.right + spacing;
        break;
    }

    // Ensure tooltip stays within viewport
    left = Math.max(spacing, Math.min(left, viewportWidth - tooltipWidth - spacing));
    top = Math.max(spacing, Math.min(top, viewportHeight - tooltipHeight - spacing));

    setCalculatedPosition({ top, left });
    setActualPosition(finalPosition);
  };

  const showTooltip = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }

    timeoutRef.current = setTimeout(() => {
      calculatePosition();
      setIsVisible(true);
    }, delay);
  };

  const hideTooltip = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setIsVisible(false);
  };

  const handleMouseEnter = () => {
    if (trigger === 'hover') {
      showTooltip();
    }
  };

  const handleMouseLeave = () => {
    if (trigger === 'hover') {
      hideTooltip();
    }
  };

  const handleClick = () => {
    if (trigger === 'click') {
      if (isVisible) {
        hideTooltip();
      } else {
        showTooltip();
      }
    }
  };

  useEffect(() => {
    const handleResize = () => {
      if (isVisible) {
        calculatePosition();
      }
    };

    const handleScroll = () => {
      if (isVisible) {
        calculatePosition();
      }
    };

    window.addEventListener('resize', handleResize);
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('resize', handleResize);
      window.removeEventListener('scroll', handleScroll);
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [isVisible]);

  const getIcon = () => {
    switch (content.type) {
      case 'feature':
        return <Star className="text-yellow-500" size={16} />;
      case 'benefit':
        return <Zap className="text-green-500" size={16} />;
      case 'tip':
        return <Lightbulb className="text-orange-500" size={16} />;
      default:
        return <Info className="text-blue-500" size={16} />;
    }
  };

  const getHeaderColor = () => {
    switch (content.type) {
      case 'feature':
        return 'text-yellow-700 bg-yellow-50 border-yellow-200';
      case 'benefit':
        return 'text-green-700 bg-green-50 border-green-200';
      case 'tip':
        return 'text-orange-700 bg-orange-50 border-orange-200';
      default:
        return 'text-blue-700 bg-blue-50 border-blue-200';
    }
  };

  const tooltip = isVisible && (
    <motion.div
      ref={tooltipRef}
      initial={{ opacity: 0, scale: 0.9, y: actualPosition === 'top' ? 10 : -10 }}
      animate={{ opacity: 1, scale: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0.9, y: actualPosition === 'top' ? 10 : -10 }}
      transition={{ duration: 0.2 }}
      className="fixed z-50 pointer-events-auto"
      style={{
        top: calculatedPosition.top,
        left: calculatedPosition.left,
        maxWidth: maxWidth
      }}
      onMouseEnter={() => trigger === 'hover' && setIsVisible(true)}
      onMouseLeave={() => trigger === 'hover' && hideTooltip()}
    >
      <Card className="shadow-xl border-2 border-gray-100">
        <CardContent className="p-0">
          {/* Header */}
          <div className={`flex items-center justify-between p-4 border-b ${getHeaderColor()}`}>
            <div className="flex items-center space-x-2">
              {getIcon()}
              <h3 className="font-semibold text-sm">{content.title}</h3>
            </div>
            {trigger === 'click' && (
              <Button
                variant="ghost"
                size="sm"
                onClick={hideTooltip}
                className="h-6 w-6 p-0 hover:bg-white/50"
              >
                <X size={12} />
              </Button>
            )}
          </div>

          {/* Content */}
          <div className="p-4 space-y-3">
            <p className="text-sm text-gray-700 leading-relaxed">
              {content.description}
            </p>

            {/* Features */}
            {content.features && content.features.length > 0 && (
              <div>
                <h4 className="text-xs font-semibold text-gray-600 mb-2 flex items-center">
                  <Star size={12} className="mr-1" />
                  Tính năng
                </h4>
                <ul className="space-y-1">
                  {content.features.map((feature, index) => (
                    <li key={index} className="text-xs text-gray-600 flex items-start">
                      <span className="text-[hsl(207,100%,40%)] mr-1">•</span>
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {/* Benefits */}
            {content.benefits && content.benefits.length > 0 && (
              <div>
                <h4 className="text-xs font-semibold text-gray-600 mb-2 flex items-center">
                  <Shield size={12} className="mr-1" />
                  Lợi ích
                </h4>
                <div className="flex flex-wrap gap-1">
                  {content.benefits.map((benefit, index) => (
                    <Badge key={index} variant="secondary" className="text-xs">
                      {benefit}
                    </Badge>
                  ))}
                </div>
              </div>
            )}

            {/* Tips */}
            {content.tips && content.tips.length > 0 && (
              <div>
                <h4 className="text-xs font-semibold text-gray-600 mb-2 flex items-center">
                  <Lightbulb size={12} className="mr-1" />
                  Mẹo hay
                </h4>
                <ul className="space-y-1">
                  {content.tips.map((tip, index) => (
                    <li key={index} className="text-xs text-gray-600 flex items-start">
                      <span className="text-orange-500 mr-1">💡</span>
                      {tip}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {/* CTA */}
            {content.cta && (
              <div className="pt-2 border-t">
                <Button
                  size="sm"
                  onClick={content.cta.action}
                  className="w-full step-gradient text-white text-xs"
                >
                  {content.cta.text}
                  <ArrowRight size={12} className="ml-1" />
                </Button>
              </div>
            )}
          </div>

          {/* Arrow */}
          <div
            className={`absolute w-3 h-3 bg-white border transform rotate-45 ${
              actualPosition === 'top' ? 'bottom-[-6px] border-b border-r' :
              actualPosition === 'bottom' ? 'top-[-6px] border-t border-l' :
              actualPosition === 'left' ? 'right-[-6px] border-r border-t' :
              'left-[-6px] border-l border-b'
            }`}
            style={{
              left: actualPosition === 'top' || actualPosition === 'bottom' ? '50%' : undefined,
              top: actualPosition === 'left' || actualPosition === 'right' ? '50%' : undefined,
              transform: `translate(${
                actualPosition === 'top' || actualPosition === 'bottom' ? '-50%' : 
                actualPosition === 'left' ? '50%' : '-50%'
              }, ${
                actualPosition === 'left' || actualPosition === 'right' ? '-50%' : 
                actualPosition === 'top' ? '50%' : '-50%'
              }) rotate(45deg)`
            }}
          />
        </CardContent>
      </Card>
    </motion.div>
  );

  return (
    <>
      <div
        ref={triggerRef}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        onClick={handleClick}
        className="inline-block"
      >
        {children}
      </div>
      <AnimatePresence>
        {tooltip && createPortal(tooltip, document.body)}
      </AnimatePresence>
    </>
  );
}

client/src/components/inline-editor.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent } from "@/components/ui/card";
import { Edit3, Save, X, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface InlineEditorProps {
  page: string;
  section: string;
  elementId: string;
  defaultContent: string;
  contentType?: "text" | "html" | "json";
  className?: string;
  tag?: keyof JSX.IntrinsicElements;
}

export function InlineEditor({
  page,
  section,
  elementId,
  defaultContent,
  contentType = "text",
  className = "",
  tag: Tag = "div"
}: InlineEditorProps) {
  const [isEditing, setIsEditing] = useState(false);
  const [content, setContent] = useState(defaultContent);
  const [tempContent, setTempContent] = useState(defaultContent);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Check if user is authenticated for CMS
  const { data: authStatus } = useQuery({
    queryKey: ["/api/auth/status"],
    retry: false,
  });

  const isAuthenticated = authStatus?.authenticated || false;

  // Fetch content from server
  const { data: serverContent } = useQuery({
    queryKey: ["/api/page-contents", page, section, elementId],
    queryFn: async () => {
      try {
        const response = await fetch(`/api/page-contents?page=${page}`);
        if (!response.ok) return null;
        const data = await response.json();
        return data.find((item: any) => 
          item.pageName === page && 
          item.section === section && 
          item.elementId === elementId
        );
      } catch (error) {
        return null;
      }
    },
    enabled: isAuthenticated,
  });

  useEffect(() => {
    if (serverContent?.content) {
      setContent(serverContent.content);
    }
  }, [serverContent]);

  const saveMutation = useMutation({
    mutationFn: async (newContent: string) => {
      if (serverContent?.id) {
        // Update existing content
        return apiRequest(`/api/page-contents/${serverContent.id}`, "PATCH", {
          content: newContent
        });
      } else {
        // Create new content
        return apiRequest("/api/page-contents", "POST", {
          pageName: page,
          section,
          elementId,
          content: newContent,
          contentType
        });
      }
    },
    onSuccess: () => {
      setContent(tempContent);
      setIsEditing(false);
      queryClient.invalidateQueries({ queryKey: ["/api/page-contents"] });
      toast({
        title: "Thành công",
        description: "Nội dung đã được lưu",
      });
    },
    onError: () => {
      toast({
        title: "Lỗi",
        description: "Không thể lưu nội dung",
        variant: "destructive",
      });
    },
  });

  const handleEdit = () => {
    setTempContent(content);
    setIsEditing(true);
  };

  const handleSave = () => {
    saveMutation.mutate(tempContent);
  };

  const handleCancel = () => {
    setTempContent(content);
    setIsEditing(false);
  };

  // If user is not authenticated, just render the content
  if (!isAuthenticated) {
    return (
      <Tag 
        className={className}
        dangerouslySetInnerHTML={contentType === "html" ? { __html: content } : undefined}
      >
        {contentType === "text" ? content : undefined}
      </Tag>
    );
  }

  return (
    <div className="relative group">
      {!isEditing ? (
        <>
          <Tag 
            className={`${className} ${isAuthenticated ? 'hover:bg-blue-50 hover:border hover:border-blue-200 transition-colors cursor-pointer' : ''}`}
            onClick={handleEdit}
            dangerouslySetInnerHTML={contentType === "html" ? { __html: content } : undefined}
          >
            {contentType === "text" ? content : undefined}
          </Tag>

          {isAuthenticated && (
            <Button
              size="sm"
              variant="outline"
              className="absolute -top-2 -right-2 opacity-0 group-hover:opacity-100 transition-opacity z-10 h-6 w-6 p-0"
              onClick={handleEdit}
            >
              <Edit3 className="w-3 h-3" />
            </Button>
          )}
        </>
      ) : (
        <Card className="border-blue-500 shadow-lg">
          <CardContent className="p-3">
            <div className="space-y-3">
              {contentType === "text" ? (
                content.length > 50 ? (
                  <Textarea
                    value={tempContent}
                    onChange={(e) => setTempContent(e.target.value)}
                    className="w-full"
                    rows={3}
                  />
                ) : (
                  <Input
                    value={tempContent}
                    onChange={(e) => setTempContent(e.target.value)}
                    className="w-full"
                  />
                )
              ) : (
                <Textarea
                  value={tempContent}
                  onChange={(e) => setTempContent(e.target.value)}
                  className="w-full font-mono text-sm"
                  rows={contentType === "html" ? 4 : 6}
                />
              )}

              <div className="flex gap-2">
                <Button
                  size="sm"
                  onClick={handleSave}
                  disabled={saveMutation.isPending}
                >
                  {saveMutation.isPending ? (
                    <Loader2 className="w-3 h-3 animate-spin mr-1" />
                  ) : (
                    <Save className="w-3 h-3 mr-1" />
                  )}
                  Lưu
                </Button>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={handleCancel}
                  disabled={saveMutation.isPending}
                >
                  <X className="w-3 h-3 mr-1" />
                  Hủy
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

