    doc.text('Văn phòng: Số 99 Hoàng Ngân - Phường Nhân Chính - Quận Thanh Xuân - Tp. Hà Nội', 105, 32, { align: 'center' });
    doc.text('Email: info@step.com.vn | Website: http://step.com.vn/', 105, 37, { align: 'center' });
