import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { 
  Zap, 
  Shield, 
  Server, 
  CheckCircle, 
  ArrowRight, 
  Globe, 
  Clock,
  Users,
  Star,
  Database,
  TrendingUp,
  Lock,
  HardDrive,
  Cpu,
  Activity,
  BarChart3,
  RefreshCw,
  FileText,
  HeadphonesIcon,
  Rocket,
  Award,
  ChevronDown,
  ChevronUp,
  Gauge,
  Cloud,
  Code2,
  Timer,
  Layers,
  Settings,
  MessageCircle,
  CircleCheck,
  CircleX,
  Sparkles,
  Crown
} from "lucide-react";
import Header from "@/components/header";
import Footer from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";

export default function HostingNVME() {
  const [animatedIOPS, setAnimatedIOPS] = useState(0);
  const [animatedSpeed, setAnimatedSpeed] = useState(0);
  const [activePackageTab, setActivePackageTab] = useState("Business");

  useEffect(() => {
    const iopsInterval = setInterval(() => {
      setAnimatedIOPS(prev => {
        if (prev >= 1500000) return 1500000;
        return prev + 25000;
      });
    }, 20);

    const speedInterval = setInterval(() => {
      setAnimatedSpeed(prev => {
        if (prev >= 3500) return 3500;
        return prev + 50;
      });
    }, 20);

    return () => {
      clearInterval(iopsInterval);
      clearInterval(speedInterval);
    };
  }, []);

  const packages = [
