}

client/src/components/inline-edit/EditTo.content:
import { useInlineEdit } from "@/contexts/InlineEditContext";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Edit3, 
  Save, 
  X, 
  Eye, 
  Loader2,
  Shield,
  PenTool,
  User,
  Users
} from "lucide-react";
import { cn } from "@/lib/utils";

const roleConfig = {
  admin: { label: 'Admin', icon: Shield, color: 'bg-red-100 text-red-700' },
  editor: { label: 'Editor', icon: PenTool, color: 'bg-blue-100 text-blue-700' },
  writer: { label: 'Writer', icon: Edit3, color: 'bg-green-100 text-green-700' },
  viewer: { label: 'Viewer', icon: Eye, color: 'bg-gray-100 text-gray-700' },
};

export function EditToolbar() {
  const { 
    isEditMode, 
    toggleEditMode, 
    canEdit, 
    userRole,
    pendingChanges,
    saveAllChanges,
    discardChanges,
    isSaving,
  } = useInlineEdit();

  const dirtyCount = Array.from(pendingChanges.values()).filter(item => item.isDirty).length;
  const role = roleConfig[userRole as keyof typeof roleConfig] || roleConfig.viewer;
  const RoleIcon = role.icon;

  if (!canEdit) return null;

  return (
    <div 
      className={cn(
        "fixed bottom-6 left-1/2 -translate-x-1/2 z-50",
        "bg-white rounded-full shadow-2xl border border-gray-200",
        "flex items-center gap-3 px-4 py-2",
        "transition-all duration-300",
        isEditMode && "ring-2 ring-blue-400"
      )}
      data-testid="edit-toolbar"
    >
      <Badge className={cn("gap-1", role.color)}>
        <RoleIcon className="w-3 h-3" />
        {role.label}
      </Badge>

      <div className="w-px h-6 bg-gray-200" />

      <Button
        variant={isEditMode ? "default" : "outline"}
        size="sm"
        onClick={toggleEditMode}
        className={cn(
          "gap-2",
          isEditMode && "bg-blue-600 hover:bg-blue-700"
        )}
        data-testid="toggle-edit-mode"
      >
        {isEditMode ? (
          <>
            <Eye className="w-4 h-4" />
            Xem
          </>
        ) : (
          <>
            <Edit3 className="w-4 h-4" />
            Chỉnh sửa
          </>
        )}
      </Button>

      {isEditMode && (
        <>
          <div className="w-px h-6 bg-gray-200" />

          {dirtyCount > 0 && (
            <Badge variant="secondary" className="bg-yellow-100 text-yellow-700">
              {dirtyCount} thay đổi
            </Badge>
          )}

          <Button
            variant="outline"
            size="sm"
            onClick={discardChanges}
            disabled={dirtyCount === 0 || isSaving}
            className="gap-2 text-gray-600"
            data-testid="discard-changes"
          >
            <X className="w-4 h-4" />
            Hủy
          </Button>

          <Button
            size="sm"
            onClick={saveAllChanges}
            disabled={dirtyCount === 0 || isSaving}
            className="gap-2 bg-green-600 hover:bg-green-700"
            data-testid="save-changes"
          >
            {isSaving ? (
              <>
                <Loader2 className="w-4 h-4 animate-spin" />
                Đang lưu...
              </>
            ) : (
              <>
                <Save className="w-4 h-4" />
                Lưu ({dirtyCount})
              </>
            )}
          </Button>
        </>
      )}
    </div>
  );
}

client/src/hooks/useAuth.ts.content:
import { useQuery } from "@tanstack/react-query";

export interface User {
  id: string;
  email: string | null;
  firstName: string | null;
  lastName: string | null;
  profileImageUrl: string | null;
  role: 'admin' | 'editor' | 'writer' | 'viewer';
  isAdmin?: boolean;
}

export function useAuth() {
  const { data: user, isLoading, error } = useQuery<User>({
    queryKey: ["/api/auth/user"],
    retry: false,
  });

  const role = user?.role || 'viewer';
  const canEdit = ['admin', 'editor'].includes(role);
  const canPublish = ['admin', 'editor'].includes(role);
  const canCreate = ['admin', 'editor', 'writer'].includes(role);
  const canManageUsers = role === 'admin';

  return {
    user,
    isLoading,
    isAuthenticated: !!user,
    error,
    role,
    canEdit,
    canPublish,
    canCreate,
    canManageUsers,
  };
}

client/src/components/inline-edit/Editab.content:
import { useState, useRef, useEffect } from "react";
import { useInlineEdit } from "@/contexts/InlineEditContext";
import { Edit3, Check, X } from "lucide-react";
import { cn } from "@/lib/utils";

interface EditableTextProps {
  id: string;
  type: 'page-content' | 'article' | 'service' | 'testimonial';
  field: string;
  value: string;
  as?: 'h1' | 'h2' | 'h3' | 'h4' | 'p' | 'span' | 'div';
  className?: string;
  multiline?: boolean;
  placeholder?: string;
}

export function EditableText({
  id,
  type,
  field,
  value,
  as: Component = 'p',
  className,
  multiline = false,
  placeholder = "Nhấp để chỉnh sửa...",
}: EditableTextProps) {
  const { isEditMode, canEdit, updatePendingChange, registerEditable, pendingChanges } = useInlineEdit();
  const [isEditing, setIsEditing] = useState(false);
  const [localValue, setLocalValue] = useState(value);
  const inputRef = useRef<HTMLInputElement | HTMLTextAreaElement>(null);
  const key = `${type}-${id}-${field}`;

  useEffect(() => {
    registerEditable(key, { id, type, field, originalValue: value });
  }, [id, type, field, value, key, registerEditable]);

  useEffect(() => {
    const item = pendingChanges.get(key);
    if (item) {
      setLocalValue(item.currentValue);
    } else {
      setLocalValue(value);
    }
  }, [pendingChanges, key, value]);

  useEffect(() => {
    if (isEditing && inputRef.current) {
      inputRef.current.focus();
      inputRef.current.select();
    }
  }, [isEditing]);

  const handleStartEdit = () => {
    if (!isEditMode || !canEdit) return;
    setIsEditing(true);
  };

  const handleSave = () => {
    updatePendingChange(key, localValue);
    setIsEditing(false);
  };

  const handleCancel = () => {
    const item = pendingChanges.get(key);
    setLocalValue(item?.originalValue || value);
    setIsEditing(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !multiline) {
      e.preventDefault();
      handleSave();
    }
    if (e.key === 'Escape') {
      handleCancel();
    }
  };

  const item = pendingChanges.get(key);
  const isDirty = item?.isDirty || false;

  if (!isEditMode) {
    return <Component className={className}>{value || placeholder}</Component>;
  }

  if (isEditing) {
    return (
      <div className="relative inline-flex items-center gap-2 w-full">
        {multiline ? (
          <textarea
            ref={inputRef as React.RefObject<HTMLTextAreaElement>}
            value={localValue}
            onChange={(e) => setLocalValue(e.target.value)}
            onKeyDown={handleKeyDown}
            onBlur={handleSave}
            className={cn(
              "w-full min-h-[100px] p-2 border-2 border-blue-500 rounded-md bg-white text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-300",
              className
            )}
            placeholder={placeholder}
          />
        ) : (
          <input
            ref={inputRef as React.RefObject<HTMLInputElement>}
            type="text"
            value={localValue}
            onChange={(e) => setLocalValue(e.target.value)}
            onKeyDown={handleKeyDown}
            onBlur={handleSave}
            className={cn(
              "w-full p-2 border-2 border-blue-500 rounded-md bg-white text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-300",
              className
            )}
            placeholder={placeholder}
          />
        )}
        <div className="flex gap-1 shrink-0">
          <button
            onClick={handleSave}
            className="p-1.5 bg-green-500 text-white rounded hover:bg-green-600 transition-colors"
            title="Lưu (Enter)"
          >
            <Check className="w-4 h-4" />
          </button>
          <button
            onClick={handleCancel}
            className="p-1.5 bg-gray-500 text-white rounded hover:bg-gray-600 transition-colors"
            title="Hủy (Esc)"
          >
            <X className="w-4 h-4" />
          </button>
        </div>
      </div>
    );
  }

  return (
    <div
      onClick={handleStartEdit}
      className={cn(
        "relative group cursor-pointer rounded transition-all",
        "hover:bg-blue-50 hover:ring-2 hover:ring-blue-300",
        isDirty && "bg-yellow-50 ring-2 ring-yellow-400",
        className
      )}
      title="Nhấp để chỉnh sửa"
    >
      <Component className={cn("inline", className)}>
        {localValue || placeholder}
      </Component>
      <span className="absolute -right-2 -top-2 opacity-0 group-hover:opacity-100 transition-opacity">
        <Edit3 className="w-4 h-4 text-blue-500 bg-white rounded-full p-0.5 shadow" />
      </span>
      {isDirty && (
        <span className="absolute -left-1 top-0 w-1 h-full bg-yellow-400 rounded-l" />
      )}
    </div>
  );
}
